var curry = require('lodash').curry;

var trace = curry(arr => {
  console.log(arr)
  return arr;
});

var compose = (f, g) => {
  return x => f(g(x))
}

var plusOne = x => {
  return x.map(x => x + 1)
}

var reverse = x => {
  return x.reverse()
}

var rp = compose(plusOne, trace, reverse)

module.exports.rp = rp;
